query:
query SearchProduct {
  products(
     first: 20

     orderBy: RELEVANCE
    filters: {

      isOutOfStock: false


      category: { include: ["phones-tablets/smartphones"] }
      merchant: { include: ["lazada","amazon","idrop-global","mytheresa"] }
    }
  ) {
    brands {
      id
      name
      slug
    }
    merchants {
      id
      name
      code
      offerId
      url
      images
    }
    categories {
      id
      name
      slug
      image
    }
    price {
      minimum {
        original
        sale
        discount
        currency
      }
      maximum {
        original
        sale
        discount
        currency
      }
    }
    edges {
      node {
        __typename
        ... on ProductDetail {
          id
          name
          images
          slug
          brand {
            id
            name
            slug
            image
          }
          views
          localPrice {
            minimum {
              original
              sale
              discount
              currency
            }
            maximum {
              original
              sale
              discount
              currency
            }
          }
          offers(first: 600) {
            totalCount
            sellerCount
            edges {
              node {
                id
                stock
                sku
                deeplink
                name
                localPrice {
                  original
                  sale
                  discount
                  currency
                }
                internationalPrice {
                  original
                  sale
                  discount
                  currency
                }
                popularity
                merchant {
                  id
                  name
                  code
                  offerId
                  url
                  images
                }
                seller {
                  name
                  rating
                }
                rating
                brand {
                  id
                  name
                  slug
                  image
                }
                images
                outOfStock
              }
              cursor
            }
            pageInfo {
              endCursor
              hasNextPage
            }
          }
        }
        ... on Offer {
          id
          stock
          sku
          deeplink
          name
          localPrice {
            original
            sale
            discount
            currency
          }
          internationalPrice {
            original
            sale
            discount
            currency
          }
          popularity
          merchant {
            id
            name
            code
            offerId
            url
            images
          }
          seller {
            name
            rating
          }
          rating
          brand {
            id
            name
            slug
            image
          }
          images
          outOfStock
        }
      }
      cursor
    }
    pageInfo {
      endCursor
      hasNextPage
    }
    totalCount
  }
}
